mapper.select:
select * from store


        and id LIKE CONCAT('%', #{id}, '%')


        and store_id like CONCAT('%', #{storeId}, '%')


        and name like CONCAT('%', #{name}, '%')


        and store_type = #{storeType}


        and store_time = #{storeTime}


        and status = #{status}


        and address like CONCAT('%', #{address}, '%')


        and identity_name like CONCAT('%', #{identityName}, '%')


        and identity_company_name like CONCAT('%', #{identityCompanyName}, '%')


        and identity_no = like CONCAT('%', #{identityNo}, '%')


        and identity_phone = like CONCAT('%', #{identityPhone}, '%')


        and identity_email = like CONCAT('%', #{identityEmail}, '%')


        and created_time = #{createdTime}


        and update_time = #{updateTime}


        and deleted = #{deleted}


        and deleted = 0

mapper.update:
update store
name = #{name}, address = #{address}, user_id = #{userId}, store_type = #{storeType}, store_time = #{storeTime}, status = #{status}, identity_name = #{identityName}, identity_company_name = #{identityCompanyName}, business_license = #{businessLicense}, identity_no = #{identityNo}, identity_font_img = #{identityFontImg}, identity_back_img = #{identityBackImg}, identity_phone = #{identityPhone}, identity_email = #{identityEmail}, update_by = #{updateBy},
where id = #{id} -- update store set name = #{name}, address = #{address}, user_id = #{userId}, store_type = #{storeType}, store_time = #{storeTime}, status = #{status}, identity_name = #{identityName}, identity_company_name = #{identityCompanyName}, business_license = #{businessLicense}, identity_no = #{identityNo}, identity_font_img = #{identityFontImg}, identity_back_img = #{identityBackImg}, identity_phone = #{identityPhone}, identity_email = #{identityEmail}, update_by = #{updateBy} where id = #{id}
