mapper.select:
select * from store


        and id LIKE CONCAT('%', #{id}, '%')


        and store_id like CONCAT('%', #{storeId}, '%')


        and store_name like CONCAT('%', #{storeName}, '%')


        and store_type = #{storeType}


        and store_time = #{storeTime}


        and status = #{status}


        and address like CONCAT('%', #{address}, '%')


        and identity_name like CONCAT('%', #{identityName}, '%')


        and identity_company_name like CONCAT('%', #{identityCompanyName}, '%')


        and identity_no like CONCAT('%', #{identityNo}, '%')


        and identity_phone like CONCAT('%', #{identityPhone}, '%')


        and identity_email like CONCAT('%', #{identityEmail}, '%')


        and created_time = #{createdTime}


        and update_time = #{updateTime}



            and deleted = #{deleted}

mapper.update:
update store
store_id = #{storeId}, store_name = #{storeName}, address = #{address}, user_id = #{userId}, store_type = #{storeType}, store_time = #{storeTime}, status = #{status}, identity_name = #{identityName}, identity_company_name = #{identityCompanyName}, business_license = #{businessLicense}, identity_no = #{identityNo}, identity_photo = #{identityPhoto}, identity_phone = #{identityPhone}, identity_email = #{identityEmail}, update_by = #{updateBy}, remark = #{remark}, deleted = #{deleted},
where id = #{id}
